    max-width: 2000px;
    text-align: center;
    padding: 20px;
    min-width: 480px;
    margin-bottom: 10px;

  }

  .projects-content h2 {
    font-size: 60px;
    color: #333;
    margin-bottom: 10px;
  }

  .projects-type {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 20px;
  }

  
  .projects-type ul li {
    margin-bottom: 10px;
    border: 1px solid #555;
    border-radius: 10px;
    font-size: 30px;
    color: #555;
    background-size: 100% 100%;
    background-image: linear-gradient(to right, #e1f3e1 0%, #e1f3 100%);
    background-size: 100% 100%;
    background-repeat: no-repeat;
    transition: background-size 0.5s ease-in-out;
  }
  tx-p {
    font-size: 20px;
    color: gray;
    flex-grow: 1;
    white-space: normal;
    margin-top: 20px;
  }

  .videogame {
    position: relative;
  }
  
  .videogame img {
    width: 600px;
    height: auto;
    border: #333;
    border-radius: 10px;
    transition: opacity 0.5s ease-in-out;
    margin-top: 30px;
    z-index: 1;
  }
  
  .videogame:hover img {
    opacity: 0.8;
  }
  
  .videogame:hover .logos-container {
    opacity: 1;
  }
  

  .skillhub {
    position: relative;
  }
  
  .skillhub img {
    width: 600px;
    height: auto;
    border: #333;
    border-radius: 10px;
    transition: opacity 0.5s ease-in-out;
    margin-top: 30px;
    margin-block-end: 30px;

  }
  
  .skillhub:hover img {
    opacity: 0.9;
  }
  
  .skillhub:hover .logos-container {
    opacity: 1;
  }
  
  .logos-container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    display: flex;
    justify-content: center;
    align-items: center;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
  }
  
  .logos-container a {
    display: inline-block;
    margin-right: 10px;
  }
  
  .logos-container img {
    width: 70px;
    height: 70px;
  }
   .projects-type img {
    width: 30px;
    height: 30px;
    margin-right: 10px;
  }


.footer {
    padding: 21px;
    background-color: plum; 
    min-width: 480px;
  margin-bottom: 10px; 
}

.footer-content {
    display: flex;
    justify-content: space-between;
    min-width: 480px;
  margin-bottom: 10px;
}

.link h3 {
    font-size: 25px;
    color: grey;
    margin-bottom: 15px;
    text-align: center;
    font-family:sans-serif;
}

