@media(max-width:480px) {
  .menu {
    padding: 15px;
}
.menu label {
    display: initial;
}
.menu .navbar {
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background-color: grey;
    display: none;
}
.menu .navbar ul li {
    width: 100%;
}
.menu:checked ~ .navbar {
    display: initial;
}

.header {
    min-height: 0vh;
}
.header-content {
    padding: 50px 15px;
}
.header-content p {
    padding: 0;
}

.education {
    padding: 15px;
}
.education-content h2 {
    padding: 0;
}
.txt-p {
    padding: 0;
}
.about {
padding: 15px;
}
.about-content {
padding: 0;
}
.projects {
padding: 15px;
}
.projects-content {
padding: 0;
}
.projects-type {
padding: 0;
display: flex;
justify-content: center;
align-items: center;
}
.txt-p {
padding: 0;
}
.skillhub,
.videogame {
display: flex;
justify-content: center; 
align-items: center; 
}

.skillhub img,
.videogame img {
padding: 0;
max-width: 100%;
height: auto;
}
}
